const Page4: React.FC = () => {
  const [rotation, setRotation] = React.useState(0);

  React.useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const rotationDegree = scrollY * 0.1; // Adjust rotation speed
      setRotation(rotationDegree);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="min-h-screen bg-[#050505] text-white overflow-x-hidden font-geist dark">
      {/* Styles for fonts and custom animations */}
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Instrument+Serif:ital@0;1&family=Inter:wght@300;400;500;600&display=swap');
          
          .font-instrument-serif {
            font-family: 'Instrument Serif', serif;
          }
          .font-geist {
            font-family: 'Inter', sans-serif;
          }
          
          .text-colorfull {
            background: linear-gradient(to right, #ff7e5f, #feb47b, #ff7e5f);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
            background-size: 200% auto;
          }
          
          .animate-gradient-x {
             animation: gradient-x 3s ease infinite;
          }

          @keyframes gradient-x {
            0%, 100% {
              background-size: 200% 200%;
              background-position: left center;
            }
            50% {
              background-size: 200% 200%;
              background-position: right center;
            }
          }

          /* Custom slow spin for the image */
          .animate-spin-slow {
            animation: spin 60s linear infinite;
          }
          
          .perspective-container {
             perspective: 500px;
          }
        `}
      </style>

      <section className="relative mx-auto flex h-full w-full flex-col overflow-hidden py-20" id="skills">
        {/* Rotating Image Section */}
        <div className="container relative mx-auto mb-10">
          <div className="h-[260px] w-full flex justify-center items-center [mask-image:linear-gradient(to_top,transparent,black_50%,black_90%,transparent)]">
            <div className="relative w-[300px] md:w-[380px] will-change-transform hover:scale-105 transition-transform duration-300">
              <img
                alt="skills cover rotating"
                className="z-10 w-full select-none rounded-full opacity-85 shadow-2xl shadow-white/10"
                draggable={false}
                src="https://framerusercontent.com/images/PNuQI9DRcL2AUdDWdNTx1C6Sroo.png"
                style={{ 
                  borderRadius: '40%', 
                  boxShadow: '0 0 50px rgba(255,255,255,0.1)',
                  transform: `rotate(${rotation}deg)`,
                  transition: 'transform 0.1s linear'
                }}
              />
            </div>
          </div>
        </div>

        {/* Heading Section */}
        <h2 className="relative text-balance font-medium text-5xl tracking-tight max-sm:px-5 sm:text-5xl md:text-6xl text-center container mx-auto z-30 mb-12 -translate-y-10">
          <p className="mb-4 font-mono font-normal text-white/60 text-xs uppercase tracking-[0.2em]">My Skills</p>
          <span className="font-instrument-serif text-white">
            The Secret{' '}
            <span className="animate-gradient-x pe-2 font-instrument-serif italic tracking-tight text-colorfull">
              Sauce
            </span>
          </span>
        </h2>

        {/* Skills Grid Section */}
        <div className="container relative mx-auto flex flex-col items-center justify-center gap-4 px-4">
          <div className="w-full max-w-5xl text-center font-geist perspective-container">
            {/* Row 1 */}
            <div className="mb-2 flex flex-wrap justify-center gap-3">
              {skills.slice(0, 13).map((skill) => (
                <SkillBadge key={skill.name} {...skill} />
              ))}
            </div>

            {/* Row 2 */}
            <div className="mb-2 flex flex-wrap justify-center gap-3">
              {skills.slice(13, 25).map((skill) => (
                <SkillBadge key={skill.name} {...skill} />
              ))}
            </div>

            {/* Row 3 */}
            <div className="mb-2 flex flex-wrap justify-center gap-3">
              {skills.slice(25).map((skill) => (
                <SkillBadge key={skill.name} {...skill} />
              ))}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

